import React, { useEffect, useState } from 'react';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet }) {
  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  // State to keep track of the currently selected view (original or upscaled)
  const [selectedView, setSelectedView] = useState('original');

  // Function to handle the view selection
  const handleViewSelect = (view) => {
    setSelectedView(view);
  };

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full bg-stone-950">
      <ul id="listLiveView-container" className="w-full h-full flex justify-center">
        {uploadedFiles.map((file, index) => {
          let originalFilename = file.originalFilename;
          let upscaledFolder = file.upscaledFilename;

          const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
          const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

          return (
            <li
              id={`listLiveView-item${index}`}
              key={index}
              className="flex justify-around gap-5 max-w-[1600px] mx-4 lg:mx-20 w-full h-full bg-blue-50 py-20 px-12"
            >
              <div className="flex justify-around gap-5 w-[75%]">
                <div
                  id={`listLiveView-item${index}-original`}
                  className={`flex flex-col items-center gap-5 w-full h-full ${
                    selectedView === 'original' ? '' : 'hidden' // Show or hide based on the selected view
                  }`}
                >
                  <h3 className="w-full max-w-[21.25em] font-bold text-[30px]">Original Image</h3>
                  <img
                    src={originalImageUrl}
                    alt={originalFilename}
                    className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md"
                  />
                </div>
                <div
                  id={`listLiveView-item${index}-upscaled`}
                  className={`flex flex-col items-center gap-5 w-full h-full ${
                    selectedView === 'upscaled' ? '' : 'hidden' // Show or hide based on the selected view
                  }`}
                >
                  <h3 className="w-full max-w-[21.25em] font-bold text-[30px]">Upscaled Image</h3>
                  <img
                    src={upscaledImageUrl}
                    alt={upscaledFolder}
                    className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md"
                  />
                </div>
              </div>

              <div id={`listLiveView-item${index}-sidebar`} className="w-[25%]">
                {/* Use onClick event handlers to switch between views */}
                <div
                  id={`listLiveView-item${index}-sidebar-original`}
                  onClick={() => handleViewSelect('original')}
                >
                  Original View
                </div>
                <div
                  id={`listLiveView-item${index}-sidebar-upscaled`}
                  onClick={() => handleViewSelect('upscaled')}
                >
                  Quality View
                </div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ListLookView;
