import React from 'react'

const Guides = () => {


    
  return (
    <div id="upscaleHome_guides" className="flex flex-col items-center bg-stone-950 gap-10 py-24">
        <div id="upscaleHome_guides-optionsAndGuides" className="w-full flex flex-col items-center gap-4">
        <div id="upscaleHome_guides-optionsAndGuides-header" className="w-full flex justify-center">
            <h3 className="text-[44px] font-bold text-white w-full max-w-[1250px]">Upscale Guides & More!</h3>
        </div>
        <div id="upscaleHome_guides-optionsAndGuides-options" className="flex gap-4 max-w-[1250px] justify-start w-full mb-6">
            {/* "Backgrounds" category button */}
            <p
            id="categoryButton"
            className={`text-white font-bold px-5 py-1 rounded-md cursor-pointer ${
                selectedCategory === "backgrounds"
                ? "bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-300 to-indigo-800"
                : "bg-stone-900 hover:bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] hover:from-sky-400 hover:to-indigo-900"
            }`}
            onClick={() => handleSelectCategory("backgrounds")}
            >
            Backgrounds
            </p>
            {/* "File Type" category button */}
            <p
            id="categoryButton"
            className={`text-white font-bold px-5 py-1 rounded-md cursor-pointer ${
                selectedCategory === "file type"
                ? "bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-300 to-indigo-800"
                : "bg-stone-900 hover:from-sky-400 hover:to-indigo-900 hover:bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))]"
            }`}
            onClick={() => handleSelectCategory("file type")}
            >
            File Type
            </p>
            {/* "Sass App" category button */}
            <p
            id="categoryButton"
            className={`text-white font-bold px-5 py-1 rounded-md cursor-pointer ${
                selectedCategory === "sass app"
                ? "bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-300 to-indigo-800"
                : "bg-stone-900 hover:from-sky-400 hover:to-indigo-900 hover:bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))]"
            }`}
            onClick={() => handleSelectCategory("sass app")}
            >
            Sass App
            </p>
        </div>

        <div id="upscaleHome_guides-optionsAndGuides-guides" className="flex flex-wrap gap-4 justify-center max-w-[1600px]">
            {guides.map((guide, index) => (
            <div className="hover:from-sky-400 hover:to-indigo-900 hover:bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] p-[1px] w-full max-w-[403px] rounded-xl cursor-pointer shadow-xl">
                <div className="bg-black w-full flex justify-center rounded-xl">
                <div key={index} className="bg-stone-900 max-w-[500px] sm:max-w-[400px] p-2 rounded-xl text-stone-200">
                    <p className="pb-2 pl-1.5 truncate text-[20px] font-bold">{guide.title}</p>
                    <div className="overflow-hidden rounded-xl" style={{ width: '900px', maxWidth: '100%' }}>
                    <div style={{ width: '100%', paddingBottom: '40%', position: 'relative' }}>
                        <img
                        src={guide.image}
                        alt={guide.title}
                        style={{
                            position: 'absolute',
                            width: '100%',
                            height: '100%',
                            objectFit: 'cover',
                        }}
                        />
                    </div>
                    </div>
                </div>
                </div>
            </div>
            ))}
        </div>

        <div id="upscaleHome_guides-button" className="flex justify-center">
            <div className="bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-400 to-indigo-900  p-[1px] w-full max-w-[240px] rounded-xl cursor-pointer group">
            <div className="bg-stone-950 w-full flex justify-center rounded-xl">
                <p className="bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-400 to-indigo-900  text-transparent bg-clip-text w-full font-bold text-[20px] mx-8 my-3">View More Guides</p>
            </div>
            </div>
        </div>
        </div>
    </div>
  )
}

export default Guides
