import React, { useState, useRef } from 'react';
import nonUpscaledImage from '../../assets/nonUpscaledImage.jpg';
import upscaledImage from '../../assets/upscaledImage.jpg';

const UpscaleHeroItem = () => {
    const [eyePosition, setEyePosition] = useState({ x: 0, y: 0, bgPosX: 0, bgPosY: 0 });
    const mainRef = useRef(null);

    // Handle the mouse movement
    const handleMouseMove = (event) => {
      const rect = mainRef.current.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;

      setEyePosition({
        x: x,
        y: y,
        // Update the background position for the magnified area
        bgPosX: -x * scaleFactor + (eyeWidth / 2),
        bgPosY: -y * scaleFactor + (eyeHeight / 2)
      });
    };

    // Dimensions of the upscaled image
    const eyeWidth = 300;
    const eyeHeight = 300;

    return (
      <div className="flex flex-col items-center justify-center">
        <div 
          ref={mainRef}
          onMouseMove={handleMouseMove}
          className="w-[700px] h-[400px] border-3 border-white rounded-3xl relative overflow-hidden flex justify-center items-center"
          style={{ backgroundImage: `url(${nonUpscaledImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
        >
          <div 
            className="absolute w-[300px] h-[300px] rounded-full"
            style={{ 
              transform: 'translate(-50%, -50%)', 
              left: `${eyePosition.x}px`, 
              top: `${eyePosition.y}px`, 
              boxShadow: `0 0 0 100vh rgba(0, 0, 0, 0.2)`,
              backgroundImage: `url(${upscaledImage})`,
              backgroundSize: `${eyeWidth * scaleFactor}px ${eyeHeight * scaleFactor}px`, // Scale the background size
              backgroundPosition: `${eyePosition.bgPosX}px ${eyePosition.bgPosY}px`,
              backgroundColor: eyePosition.bgPosX === 0 && eyePosition.bgPosY === 0 ? 'rgba(0, 0, 0, 0.7)' : 'transparent',
            }}
          />
        </div>
        <h1 className="text-white font-mono">Hover to reveal image</h1>
      </div>
    );
};

export default UpscaleHeroItem;
