import React, { useRef, useState, useEffect } from 'react'
import UpscaleMarketingSlide from './UpscaleSlides/UpscaleMarketingSlide';
import imageOne from '../../assets/nonUpscaledImage.jpg'
import imageTwo from '../../assets/upscaledImage.jpg'

const UpscaleImageSlider = () => {

  const [selected, setSelected] = useState("Marketing");
  
  // Define categories with associated image paths
  const categories = {
    "Marketing": { imageOne: imageOne, imageTwo: imageTwo },
    "Photography": { imageOne: imageOne, imageTwo: imageTwo },
    "Individuals": { imageOne: imageOne, imageTwo: imageTwo },
    "E-Commerce": { imageOne: imageOne, imageTwo: imageTwo },
    "Developers": { imageOne: imageOne, imageTwo: imageTwo },
    "Enterprise": { imageOne: imageOne, imageTwo: imageTwo },
    "Real Estate": { imageOne: imageOne, imageTwo: imageTwo },
  };
  
  const [photoItems, setPhotoItems] = useState(Object.keys(categories));
  
  // Ref to track the slider container
  const sliderPhotoRef = useRef(null);
  
  const handleSelect = (item) => {
    setSelected(item);
  };
  
  useEffect(() => {
    if (selected !== null && sliderPhotoRef.current) {
      const slider = sliderPhotoRef.current;
      const selectedElement = slider.querySelector(`[data-item="${selected}"]`);
  
      if (selectedElement) {
        // Calculate the position to scroll to
        const scrollPosition = selectedElement.offsetLeft - slider.offsetLeft;
  
        slider.scrollTo({
          left: scrollPosition,
          behavior: 'smooth'
        });
      }
    }
  }, [selected]);

  return (
    <div id="upscaleHome_photoSlider" className="flex flex-col items-center bg-stone-950 md:pb-20 md:py-0 py-20">
          
      <div id="upscaleHome_photoSlider-header" className="flex flex-col w-full items-center px-4">
        <h3 className="font-bold text-[44px] text-white">Not Just Any Other Background Removal Tool</h3>
        <p className="font-bold text-[20px] text-white">Get precise and quick image transformations regardless of what industry you belong to!</p>
      </div>
      
      <div id="upscaleHome_photoSlider-selectSlider" className="flex overflow-x-auto gap-28 max-w-[90vw] lg:max-w-[70vw] hide-scrollbar text-white my-16 border-b-[1px] border-gray-500 px-4" ref={sliderPhotoRef}>
      {photoItems.map((item, index) => (
        <p
          key={index}
          data-item={item}
          onClick={() => handleSelect(item)}
          className={`cursor-pointer px-3 py-2 my-0 min-w-[140px] ${selected === item ? 'border-b-8 border-purple-500 font-bold' : ''}`}
        >
          {item}
        </p>
      ))}
      </div>
      
      <div id="upscaleHome_photoSlider-imageSlider">
        <UpscaleMarketingSlide
          category={selected}
          imageOne={categories[selected].imageOne} 
          imageTwo={categories[selected].imageTwo} 
        />
      </div>

    </div>
  )
}

export default UpscaleImageSlider
