import React, { useEffect, useState } from 'react';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet, isKeywordsSet, isQualitySet }) {
    const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full">
      <ul id="listLiveView-container" className="grid grid-cols-2 w-full h-full gap-4">
        {uploadedFiles.map((file, index) => {
          let filename;
          let folder;
          isTitleSet = false;

          if (isTitleSet) {
            if (isUpscaled) { 
              folder = 'upscaled';
              filename = `${file.sanitizedTitle}.jpg`;
            }
            else {
              folder = 'uploads';
              filename = file.originalFilename;
            }
          } else {
            if (isUpscaled) {
              folder = 'upscaled';
              filename = file.originalFilename;
            } else {
              folder = 'uploads';
              filename = file.originalFilename;
            }
          }
          
          const imageUrl = `${serverOrigin}/${folder}/${filename}`;
          return (
            <li id={`listLiveView-item${index}`} key={index} className="flex gap-5 mx-5">
              <div className="w-full max-w-[220px]">
                <img src={imageUrl} alt={filename} className="object-cover w-full h-full max-w-[200px] rounded-md" />
              </div>
              {/* Title and keywords editor */}
              <div className="flex flex-col justify-evenly">
                <span className="flex gap-16">
                  <span className="font-bold">Title:</span>
                  <p id="titleEditor" value={editedFiles[index]?.editedTitle || ''} onChange={(event) => handleTitleChange(index, event)}></p>
                </span>
                <span className="pb-10 flex gap-6">
                  <span className="font-bold">Keywords:</span>
                  <p className="keywordEditor" value={editedFiles[index]?.editedKeywords || ''} onChange={(event) => handleKeywordsChange(index, event)} />
                </span>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ListLookView;
