import React, { useState } from 'react'
import NavBar from '../components/global/NavBar'
import Footer from '../components/global/Footer'
import OtherProducts from '../components/global/OtherProducts'
import UpscaleImageInput from '../components/upload/UpscaleImageInput'

const UpscaleUploadPage = () => {

  const [isUploadClicked, setIsUploadClicked] = useState(false);
  const [editedFiles, setEditedFiles] = useState([]);
  const [isUpscaled, setIsUpscaled] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isTitleSet, setIsTitleSet] = useState(false);
  const [isKeywordsSet, setIsKeywordsSet] = useState(false);
  const [isQualitySet, setIsQualitySet] = useState(false);
  
  const onUploadClicked = (files, upscaled, quality) => {
      setUploadedFiles(files);
      setIsUpscaled(upscaled);
      setIsUploadClicked(true);
      setIsQualitySet(quality);
  };

  useEffect(() => {
    if (uploadedFiles.length > 0) {
        onUploadClicked(uploadedFiles, isUpscaled, isTitleSet, isKeywordsSet, isQualitySet);
    }
  }, [uploadedFiles, isUpscaled, isTitleSet, isKeywordsSet, isQualitySet]);

  return (
    <div>
      <NavBar />
      <UpscaleImageInput onUploadClicked={onUploadClicked} />
      <OtherProducts />
      <Footer />
    </div>
  )
}

export default UpscaleUploadPage
