import React, { useRef, useState, useEffect } from 'react';
import uploadIcon from '../../assets/upload-icon.png';

/**
 * Component for updating image input with drag and drop functionality.
 * @param {Function} onUploadClicked - Callback function triggered on upload.
 */
function UpdateImageInput({ onUploadClicked }) {
    // Refs for DOM elements
    const dropzoneRef = useRef(null);
    const fileInputRef = useRef(null);
    const fileListRef = useRef(null);

    // State variables
    const [uploadedFiles, setUploadedFiles] = useState([]);
    const [isUpscaled, setIsUpscaled] = useState(false);
    const [isQualitySet, setIsQualitySet] = useState(false);
    const [isUploadClicked, setIsUploadClicked] = useState(false);
    const [isFileAdded, setIsFileAdded] = useState(false);
    const [isFolderUploaded, setIsFolderUploaded] = useState(false);

    // Server origin based on the current window location
    const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

    // Effect hook to handle drag and drop events
    useEffect(() => {
        // Drag and drop event handlers
        const handleDragEnter = (e) => {
            e.preventDefault();
        };

        const handleDragOver = (e) => {
            e.preventDefault();
        };

        const handleDrop = async (e) => {
            e.preventDefault();
        
            const files = e.dataTransfer.files;
            if (files.length === 0) {
                return; // No files dropped, exit the function
            }
        
            // Display file information
            updateFileListDisplay(files);
        
            // Prepare the formData with the files
            const formData = new FormData();
            for (const file of files) {
                formData.append('files', file);
            }
        
            // Append additional form data (if any)
            formData.append('upscaleImages', 'true');
            formData.append('qualitySet', 'true');
        
            // Attempt to send files to the server
            try {
                const response = await fetch(`${serverOrigin}/uploads`, {
                    method: 'POST',
                    body: formData,
                });
        
                if (response.ok) {
                    console.log('Files uploaded successfully');
                    // Process the response and update state accordingly
                    // ...
                    onUploadClicked(/* Pass necessary data */);
                } else {
                    console.error('Failed to upload files');
                    // Handle error UI feedback
                }
            } catch (error) {
                console.error('Error uploading files:', error);
                // Handle error UI feedback
            }
        };

        const handleChange = (e) => {
            handleFiles(e.target.files);
            handleSubmit(e);
        };

        // Function to update file list display
const updateFileListDisplay = (files) => {
    fileListRef.current.innerHTML = ''; // Clear previous content
    let totalSize = 0;
    let isFolder = false;
    for (const file of files) {
        totalSize += file.size;
        if (file.webkitRelativePath) {
            isFolder = true;
        }
    }
    setIsFolderUploaded(isFolder);
    const displayText = isFolder ?
        `${files.length} ${files.length < 2 ? 'file' : 'files'}, ${formatBytes(totalSize)} total size` :
        `${files.length} files, ${formatBytes(totalSize)} total size`;
    fileListRef.current.textContent = displayText;
};

        // DOM elements
        const dropzone = dropzoneRef.current;

        // Event listeners for drag and drop
        dropzone.addEventListener('dragenter', handleDragEnter);
        dropzone.addEventListener('dragover', handleDragOver);
        dropzone.addEventListener('drop', handleDrop);
        fileInputRef.current.addEventListener('change', handleChange);

        // Cleanup: remove event listeners
        return () => {
            dropzone.removeEventListener('dragenter', handleDragEnter);
            dropzone.removeEventListener('dragover', handleDragOver);
            dropzone.removeEventListener('drop', handleDrop);
            fileInputRef.current.removeEventListener('change', handleChange);
        };
    }, [isUploadClicked, dropzoneRef, fileInputRef]);

    // Function to handle uploaded files
    const handleFiles = async (files) => {
        // Clear file list display
        fileListRef.current.innerHTML = '';
        let totalSize = 0;
        let isFolder = false;

        // Check if any of the files represent a folder
        for (const file of files) {
            totalSize += file.size;
            if (file.webkitRelativePath) {
                isFolder = true;
            }
        }

        // Set state based on whether a folder is uploaded
        setIsFolderUploaded(isFolder);

        if (isFolder) {
            // Display folder name and total size
            const folderName = files[0].webkitRelativePath.split('/')[0];
            fileListRef.current.textContent = `${files.length} ${files.length < 2 ? 'file' : 'files' }, ${formatBytes(totalSize)} total size`;
        } else {
            // Display number of files and their total size
            fileListRef.current.textContent = `${files.length} files, ${formatBytes(totalSize)} total size`;
        }

        // Set state to indicate whether files are added
        setIsFileAdded(files.length > 0);
        setIsUploadClicked(true);

        // Automatically send files to the server for processing
        const formData = new FormData();
        for (const file of files) {
            formData.append('files', file);
        }

        try {
            // Send a POST request to the server
            const response = await fetch(`${serverOrigin}/processFiles`, {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                console.log('Files processed successfully');
                // Parse and set the processed data
                const processedData = await response.json();
                setUploadedFiles(processedData);

                // Fetch updated photoList.json
                const photoListResponse = await fetch('http://localhost:3001/photoList');
                const newPhotoList = await photoListResponse.json();
                setUploadedFiles(newPhotoList);

                // Trigger the callback function with uploaded data and settings
                const upscaleImages = 'true';
                const qualitySet = 'true';
                onUploadClicked(newPhotoList, upscaleImages, qualitySet);

                // Trigger the form submission
                document.getElementById('folderUpload-formBody').submit();
            } else {
                console.error('Failed to process files');
                // Handle error UI feedback
            }
        } catch (error) {
            console.error('Error processing files:', error);
            // Handle error UI feedback
        }
    };

    // Function to format bytes into readable size
    const formatBytes = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    // Function to handle form submission
    const handleSubmit = async () => {
        // Create FormData from the form
        const formData = new FormData(document.getElementById('folderUpload-formBody'));

        // Set isUpscaled and isQualitySet based on checkbox values
        const upscaleImages = 'true';
        setIsUpscaled(upscaleImages);
        const qualitySet = 'true';
        setIsQualitySet(qualitySet);

        // Append qualitySet and upscaleImages to FormData
        formData.append('qualitySet', qualitySet);
        formData.append('upscaleImages', upscaleImages);

        try {
            // Send a POST request to upload files
            const response = await fetch('http://localhost:3001/uploads', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                console.log('Folder uploaded successfully');
                // Get the response body
                const text = await response.text();
                console.log("Server Response:", text);

                // Fetch updated photoList.json
                const photoListResponse = await fetch('http://localhost:3001/photoList');
                const newPhotoList = await photoListResponse.json();
                setUploadedFiles(newPhotoList);

                // Trigger the callback function with uploaded data and settings
                onUploadClicked(newPhotoList, upscaleImages, qualitySet);
            }
        } catch (error) {
            console.error('There was a problem with the upload:', error);
        }
    };

    return (
        <div className="flex lg:flex-row flex-col w-full transition-container bg-stone-950 items-center justify-center py-28 -mb-12">
            
            <div className="image-input h-full w-full pt-5 pb-7 flex flex-col items-center px-4 gap-14">

                <h1 className='text-[50px] lg:max-w-[26ch] font-bold text-indigo-200 text-center'>Upload an image, video, or folder to upscale the quality</h1>

                <form id="folderUpload-formBody" onSubmit={handleSubmit} encType="multipart/form-data" className='min-w-full lg:min-w-[600px] lg:max-h-[600px]'>

                    {/* DROP ZONE */}
                    <div
                        ref={dropzoneRef}
                        className={`bg-stone-900 px-8 py-20 text-center rounded-lg border-dashed border-2 hover:border-indigo-400 transition duration-300 ease-in-out transform hover:shadow-lg ${isFileAdded ? 'border-indigo-400' : 'border-indigo-200'}`}
                    >
                        <label htmlFor="fileInput" className="cursor-pointer flex flex-col items-center space-y-2">
                            <svg className={`w-16 h-16 text-indigo-200 group-hover:text-indigo-400 transition duration-300 ease-in-out transform ${isFileAdded ? 'text-indigo-400' : 'text-indigo-200'}`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
                            </svg>
                            <span className={`text-indigo-200 group-hover:text-indigo-400 transition duration-300 ease-in-out transform ${isFileAdded ? 'text-indigo-400' : 'text-indigo-200'}`}>Drag and drop your files here</span>
                            <span className={`text-indigo-200 group-hover:text-indigo-400 text-sm transition duration-300 ease-in-out transform ${isFileAdded ? 'text-indigo-400' : 'text-indigo-200'}`}>(or click to select)</span>
                        </label>
                        <input id="fileInput" ref={fileInputRef} type="file" className="hidden file-list" name="folderUpload" webkitdirectory="true" directory="true" multiple />
                    </div>
                    

                    {/* OPTION BUTTONS */}
                    <div className="flex w-full items-center justify-center gap-3 cursor-not-allowed">
                        {/* REFERENCE BOX */}
                        <div className="flex flex-col w-full items-center gap-3 ml-1">
                            <div className="text-center w-full text-indigo-950 ring-indigo-400 bg-indigo-300 bg-opacity-50 ring-2 py-1.5 h-[36px]" ref={fileListRef}></div>                    
                        </div>

                    </div>

                </form>
            </div>
        </div>
    );
};

export default UpdateImageInput;
