const App = () => {
  // State to track the selected item and its index
  const [selected, setSelected] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [items, setItems] = useState(["Marketing", "Photography", "E-Commerce", "Developers", "Enterprise", "Individuals", "Professional", "Real Estate", "Media", "Car Dealership"]);

  // Ref to track the slider container
  const sliderRef = useRef(null);

  // Initialize smooth scrollbar
  useEffect(() => {
    const scrollbar = Scrollbar.init(sliderRef.current, { damping: 0.05 });

    return () => {
      scrollbar.destroy();
    };
  }, []);

  // Function to handle item selection
  const handleSelect = (item, index) => {
    setSelected(item);
    setSelectedIndex(index);

    // Smooth scroll to the selected item
    if (sliderRef.current) {
      const selectedElement = sliderRef.current.querySelector(`[data-item="${item}"]`);
      if (selectedElement) {
        // Calculate horizontal scroll position
        const scrollX = selectedElement.offsetLeft - (sliderRef.current.offsetWidth / 2 - selectedElement.offsetWidth / 2);
        Scrollbar.get(sliderRef.current).scrollTo(scrollX, 0, 600);
      }
    }
  };

  // Effect to reorder items when one is selected
  useEffect(() => {
    if (selected !== null) {
      // Calculate the new order of items
      const middleIndex = Math.floor(items.length / 2);
      const distance = selectedIndex - middleIndex;
      let newItems;

      if (distance > 0) {
        // Move selected item towards the middle from the right
        newItems = [...items.slice(distance), ...items.slice(0, distance)];
      } else if (distance < 0) {
        // Move selected item towards the middle from the left
        newItems = [...items.slice(items.length + distance), ...items.slice(0, items.length + distance)];
      } else {
        // Item is already in the middle
        newItems = [...items];
      }

      setItems(newItems);
    }
  }, [selected, selectedIndex]);


  // Effect to update the DOM after items are re-ordered
  useEffect(() => {
    const selectedElement = sliderRef.current.querySelector(`[data-item="${selected}"]`);
    selectedElement && selectedElement.scrollIntoView({ behavior: 'smooth', block: 'nearest', inline: 'center' });
  }, [items]);

  return (
    <div>
      {/* NAV */}

        <div id="upscaleGlobal_nav" className="sticky top-0 h-[70px] w-full flex justify-between items-center bg-stone-950 px-6 border-b-[1px] border-gray-500">
          
          <img src="https://cadogy.com/assets/cadogy-shield-cf2a447d.svg" height="60" width="60" alt="" />
          
          <div className="flex gap-12 items-center">
            <div className="flex gap-16 font-bold text-gray-300">
              <span>
                <p>Upscale Images & Videos</p>
              </span>
              <span>
                <p>Features</p>
              </span>
              <span>
                <p>FAQs</p>
              </span>
              <span>
                <p>Blog</p>
              </span>

            </div>
            <div className="flex gap-2 text-[16px]">
              <span className="px-5 py-2 rounded-lg hover:bg-stone-900">
                <p className="text-gray-500">Login</p>
              </span>
              <span className="bg-white px-5 py-2 rounded-lg bg-gradient-to-tl from-purple-500 to-pink-500 text-white font-bold">
                <p className="text-gray-300">Signup</p>
              </span>
            </div>
          </div>
          
        </div>

      {/* HERO */}
        <div id="upscaleHome_hero" className="w-full flex justify-around items-center bg-stone-950">
          
          <div id="upscaleHome_hero-left" className="w-full flex justify-center">
            
            <video autoPlay loop className="w-full max-w-[600px]">
              <source src="https://cdn.pixelbin.io/v2/dummy-cloudname/original/erasebg_assets/upload_page/EraseBG_Hero.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            
          </div>
          <div id="upscaleHome_hero-right" className="w-full my-20">
            <div>
              <h1 className="text-white font-bold text-[56px] max-w-[650px] leading-none">Upscale Image & Video Quality For <span className="bg-gradient-to-tl from-purple-500 to-pink-500 text-transparent bg-clip-text">Free</span></h1>
              <h3 className="text-white text-[28px] max-w-[650px] my-4">Experience accurate background removal. Sign up today to avail your first 3 credits for free!</h3>
            </div>
            
            {/* uploader here */}
            <img src="https://i.gyazo.com/44edfdbeb3946b2a12827479cd831774.png" alt="" srcset="" className="max-w-[650px] mt-20" />
          
          </div>
          
        </div>
      {/* SLIDER IMAGE */}

        <div id="upscaleHome_slider" className="flex flex-col items-center">
          
          <div id="upscaleHome_slider-header" className="flex flex-col w-full items-center">
            
            <h3 className="font-bold text-[44px]">Not Just Any Other Background Removal Tool</h3>
            <p className="font-bold text-[20px]">Get precise and quick image transformations regardless of what industry you belong to!</p>
            
          </div>
          
          <div id="upscaleHome_slider-selectSlider" className="flex flex-row overflow-x-auto gap-4 hide-scrollbar whitespace-nowrap" ref={sliderRef}>
            {items.map((item, index) => (
              <p
                key={index}
                data-item={item}
                className={`cursor-pointer px-3 py-2 my-8 min-w-[140px] ${selected === item ? 'border-b-8 border-purple-500 font-bold' : ''}`}
                onClick={() => handleSelect(item, index)}
              >
                {item}
              </p>
            ))}
          </div>
          
          <div id="upscaleHome_slider-imageSlider">
            
            <img src="https://i.gyazo.com/d543dca3226cd06b09de7e9c6b93b7e8.png" alt="" />
            
          </div>

        </div>

      {/* BACK-AND-FORTH PIECES */}
      {/* SLIDER VIDEO */}
      {/* BLOGS (as reviews) */}
      {/* HOW-TO GUIDES */}
      {/* OTHER PRODUCTS */}
      {/* FAQs */}
      {/* BULLSHIT */}
      {/* FOOTER */}
    </div>
  )
}

export default App
