import React, { useEffect, useState } from 'react';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet }) {
  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full bg-stone-950">
      <ul id="listLiveView-container" className="w-full h-full flex justify-center">
        {uploadedFiles.map((file, index) => {
          let originalFilename = file.originalFilename;
          let upscaledFolder = file.upscaledFilename;
          
          const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
          const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

          return (
            <li id={`listLiveView-item${index}`} key={index} className="flex justify-around gap-5 max-w-[1600px] mx-4 lg:mx-20 w-full h-full bg-blue-50">
              {/* <div className="w-full max-w-[220px]">
                <img src={imageUrl} alt={filename} className="object-cover w-full h-full max-w-[200px] rounded-md" />
              </div>
              <div className="flex flex-col justify-evenly">
                <span className="flex gap-16">
                  <span className="font-bold">Title:</span>
                  <p id="titleEditor" value={file.originalFilename}></p>
                  <p id="titleEditor" value={file.filesize}></p>
                  <p id="titleEditor" value={file.dimensions}></p>
                </span>
              </div> */}
              <div id={`listLiveView-item${index}-original`} className='flex flex-col items-center w-full h-full'>
                <h3 className='w-full max-w-[14.5em] font-bold text-[30px]'>Original Image</h3>
                <img src={originalImageUrl} alt={originalFilename} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
              </div>
              <div id={`listLiveView-item${index}-upscaled`} className='flex flex-col items-center w-full h-full'>
                <h3 className='w-full max-w-[14.5em] font-bold text-[30px]'>Upscaled Image</h3>
                <img src={upscaledImageUrl} alt={upscaledFolder} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ListLookView;
