import React, { useEffect, useRef, useState } from 'react';
import uploadIcon from '../assets/upload-icon.png';

function UploadPopup(title, subheading, description) {
const dropzoneRef = useRef(null);
const fileInputRef = useRef(null);
const fileListRef = useRef(null);

const [uploadedFiles, setUploadedFiles] = useState([]);
const [isUpscaled, setIsUpscaled] = useState(false);
const [isTitleSet, setIsTitleSet] = useState(false);
const [isUploadClicked, setIsUploadClicked] = useState(false);
const [isFileAdded, setIsFileAdded] = useState(false);

const [editedFiles, setEditedFiles] = useState([]);
const [isFolderUploaded, setIsFolderUploaded] = useState(false);

const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

useEffect(() => {
const initialEditedFiles = uploadedFiles.map(file => ({
...file,
editedTitle: file.generatedFilename,
editedKeywords: file.keywords
}));
setEditedFiles(initialEditedFiles);
}, [uploadedFiles]);

const handleTitleChange = (index, event) => {
const updatedFiles = [...editedFiles];
updatedFiles[index].editedTitle = event.target.value;
setEditedFiles(updatedFiles);
};

const generateCsvFile = async () => {
try {
const response = await fetch(`${serverOrigin}/generate-csv`);
if (response.ok) {
console.log('CSV file generated successfully.');
} else {
console.error('Failed to generate CSV file.');
}
} catch (error) {
console.error('Error generating CSV file:', error);
}
};

const downloadRecords = async () => {
try {
const response = await fetch(`${serverOrigin}/download-records`);
if (response.ok) {
// Retrieve the binary blob from the response
const blob = await response.blob();
// Create a link element and trigger download
const downloadUrl = URL.createObjectURL(blob);
const a = document.createElement('a');
a.href = downloadUrl;
a.download = 'records.zip';
document.body.appendChild(a);
a.click();
a.remove();
URL.revokeObjectURL(downloadUrl); // Clean up the URL object
console.log('Records downloaded successfully.');
} else {
console.error('Failed to download records.');
}
} catch (error) {
console.error('Error downloading records:', error);
}
};

const handleKeywordsChange = (index, event) => {
const updatedFiles = [...editedFiles];
updatedFiles[index].editedKeywords = event.target.value;
setEditedFiles(updatedFiles);
};

useEffect(() => {
const handleDragEnter = (e) => {
    e.preventDefault();
    if (!isUploadClicked) {
        dropzoneRef.current.classList.add('border-green-600');
    }
};

const handleDragOver = (e) => {
    e.preventDefault();
    if (!isUploadClicked) {
        dropzoneRef.current.classList.add('border-green-600');
    }
};

const handleDragLeave = () => {
    if (!isUploadClicked) {
        dropzoneRef.current.classList.remove('border-green-600');
    }
};

const handleDrop = (e) => {
    e.preventDefault();
    if (!isUploadClicked) {
        dropzoneRef.current.classList.remove('border-green-600');
    }
    handleFiles(e.dataTransfer.files);
};

const handleChange = (e) => {
    handleFiles(e.target.files);
};

const dropzone = dropzoneRef.current;

dropzone.addEventListener('dragenter', handleDragEnter);
dropzone.addEventListener('dragover', handleDragOver);
dropzone.addEventListener('dragleave', handleDragLeave);
dropzone.addEventListener('drop', handleDrop);
fileInputRef.current.addEventListener('change', handleChange);

return () => {
    dropzone.removeEventListener('dragenter', handleDragEnter);
    dropzone.removeEventListener('dragover', handleDragOver);
    dropzone.removeEventListener('dragleave', handleDragLeave);
    dropzone.removeEventListener('drop', handleDrop);
    fileInputRef.current.removeEventListener('change', handleChange);
};
}, [isUploadClicked]);

const handleFiles = (files) => {
    fileListRef.current.innerHTML = '';
    let totalSize = 0;
    let isFolder = false;

    for (const file of files) {
        totalSize += file.size;
        if (file.webkitRelativePath) { // this property is usually present for files in a folder
            isFolder = true;
        }
    }

    setIsFolderUploaded(isFolder);

    if (isFolder) {
        const folderName = files[0].webkitRelativePath.split('/')[0]; // Gets the folder name
        fileListRef.current.textContent = `${folderName} (${formatBytes(totalSize)})`;
    } else {
        for (const file of files) {
            const listItem = document.createElement('div');
            const maxNameLength = 33;
            let truncatedName = file.name.length > maxNameLength ? file.name.substring(0, maxNameLength) + '...' : file.name;
            listItem.textContent = `${truncatedName} (${formatBytes(file.size)})`;
            fileListRef.current.appendChild(listItem);
        }
    }

    setIsFileAdded(files.length > 0);
};

const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    const handleSubmit = async (event) => {
    event.preventDefault();
    setIsUploadClicked(true);

    const formData = new FormData(event.target);
    const upscaleImages = formData.get('upscaleImages') === 'true';
    setIsUpscaled(upscaleImages);
    const titleSet = formData.get('genTitle') === 'true';
    setIsTitleSet(titleSet);

    try {
        const response = await fetch('http://localhost:3001/uploads', {
            method: 'POST',
            body: formData,
        });
        if (response.ok) {
            console.log('Folder uploaded successfully');
            const text = await response.text();
            console.log("Server Response:", text);
            const photoListResponse = await fetch('http://localhost:3001/photoList');
            const newPhotoList = await photoListResponse.json();
            setUploadedFiles(newPhotoList);
        }
    } catch (error) {
        console.error('There was a problem with the upload:', error);
    }
};

const handleJsonEditorSubmit = async (event) => {
    event.preventDefault();
    const updatedData = editedFiles.map(file => ({
    originalFilename: file.originalFilename,
    generatedFilename: file.editedTitle,
    keywords: file.editedKeywords
    }));
    try {
    const response = await fetch('http://localhost:3001/update', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify(updatedData)
    });
    if (response.ok) {
    console.log('JSON data updated successfully');
    const text = await response.text();
    console.log('Server Response:', text);
    } else {
    const text = await response.text();
    console.error('Error updating JSON data:', text);
    }
    } catch (error) {
    console.error('There was a problem with updating the JSON data:', error);
    }
};

return (
<main className="absolute top-5 z-[999] bg-green-100 rounded-xl flex justify-around items-center flex-col min-h-[95vh] w-[95vw] lg:px-20 sm:px-10 px-4">

{/* POPUP HEADER */}
<div>
    <div className="flex text-white drop-shadow-lg w-full">
        <h3 className="text-[40px] mr-4 font-bold">Title -</h3>
        <h4 className="text-[30px] self-center mt-1 font-semibold underline">Subheading</h4>
    </div>
    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quia vel ducimus autem? Qui, cum corporis vero provident eaque impedit eligendi consectetur laborum odio, nam sunt, aliquam ut reiciendis nihil exercitationem!</p>
</div>

<div className={`flex w-full transition-container ${isUploadClicked ? 'justify-start' : 'justify-end'}`}>
    
    {/* IMAGE INPUT */}
    
    <div className={`image-input w-full lg:max-w-[600px] p-9 bg-white rounded-lg shadow-lg`}>
        {/* HEADER STYLING */}
        <div class="relative py-4 -my-4 z-0">
            <div class="absolute inset-0 flex items-center z-0">
                <div class="w-full border-b border-gray-300 -mt-2"></div>
            </div>
            <div class="relative flex justify-left items-center">
                <span class="bg-white pr-4 text-left text-2xl sm:text-2xl font-semibold mb-4 text-gray-800">File Drop and Upload</span>
            </div>
        </div>

        <form id="folderUpload-formBody" onSubmit={handleSubmit} encType="multipart/form-data z-10">
                    
            <div id="folderUpload-optionBar" className="grid md:grid-cols-2 justify-between z-10">
                <span className="flex justify-stretch items-center gap-3"><input type="checkbox" id="folderUpload-upscaleImages" name="upscaleImages" value="true"></input>
                <p>AI Upscale Images</p></span>
                <span className="flex justify-stretch items-center gap-3"><input type="checkbox" id="folderUpload-genTitle" name="genTitle" value="true"></input>
                <p>AI Generate Title</p></span>
                <span className="flex justify-stretch items-center gap-3"><input type="checkbox" id="folderUpload-genKeyword" name="genKeyword" value="true"></input>
                <p>AI Generate Keywords</p></span>
                <span className="flex justify-stretch items-center gap-3"><input type="checkbox" id="folderUpload-useFilenames" name="useFilenames" value="true"></input>
                <p>Use Filenames in Title Generation</p></span>
                <span className="flex justify-stretch items-center gap-3"><input type="checkbox" id="folderUpload-genQuality" name="genQuality" value="true"></input>
                <p>AI Generate Quality Score</p></span>
            </div>
                    

            <div
                ref={dropzoneRef}
                className={`bg-gray-100 px-8 py-4 text-center rounded-lg border-dashed border-2 hover:border-green-600 transition duration-300 ease-in-out transform hover:shadow-md group my-4 ${isUploadClicked ? 'border-green-600' : 'border-grey-300'}`}
                >
                <label htmlFor="fileInput" className="cursor-pointer flex flex-col items-center space-y-2">
                    <svg className="w-16 h-16 text-gray-400 group-hover:text-green-600 transition duration-300 ease-in-out transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
                    </svg>
                    <span className="text-gray-600">Drag and drop your files here</span>
                    <span className="text-gray-500 text-sm">(or click to select)</span>
                </label>
                <input id="fileInput" ref={fileInputRef} type="file" className="hidden" name="folderUpload" webkitdirectory="true" directory="true" multiple />
            </div>
            <div className="flex flex-col w-full items-center gap-8">
                <div className="text-center w-full text-slate-400 ring-slate-200 ring-2 py-1.5 h-[36px]" ref={fileListRef}></div>
            
                <input 
                    id="folderUpload-submitButton" 
                    type="submit" 
                    value="Upload" 
                    className={`bg-green-300 py-2 rounded-md w-full ${!isFileAdded ? 'opacity-50 cursor-not-allowed' : ''}`}
                    disabled={!isFileAdded} 
                />
            </div>
        
        </form>
        
    </div>
    
    

    
</div>
        
<div className={`flex w-full transition-container ${isUploadClicked ? 'opacity-100' : 'opacity-0'}`}>
    <form id="jsonEditor-formBody" onSubmit={handleJsonEditorSubmit} encType="multipart/form-data" className="flex flex-col items-center gap-4">
        <div className="w-[100%] flex justify-center">
            <ul id="liveLookList" className="flex flex-wrap w-full h-full gap-5">
            {uploadedFiles.map((file, index) => {
                // Debug: Print out the image URL
                const imageUrl =
                `${serverOrigin}${isUpscaled ? '/upscaled' : '/uploads'}/${isUpscaled && isTitleSet ? `${file.sanitizedTitle}.jpg` : `${file.originalFilename}`}`;
                console.log(`Image URL: ${imageUrl}`);

                return (
                <li key={index} className="flex gap-5 mx-5">
                    <div className="w-full max-w-[360px]">
                    <img src={imageUrl} alt={file.generatedFilename} className="object-cover w-full h-full max-w-[300px] max-h-[200px] -mb-[1.38em] rounded-md" />

                    <div className="flex justify-between w-full">
                        <span className="bg-white pl-1 pr-2 rounded-tr-md">
                        <span className="font-bold">Size:</span> {isUpscaled ? file.upscaledFilesize : file.filesize}
                        </span>
                        <span className="bg-white pl-2 pr-1 rounded-tl-md flex gap-2">
                        <span className="font-bold">Quality Score:</span>
                        <span className="">{parseFloat(file.qualityScore).toFixed(4)}</span>
                        </span>
                    </div>
                    </div>

                
                    <div className="flex flex-col justify-evenly">
                        <span className="flex gap-16">
                        <span className="font-bold">Title:</span>
                        <input id="titleEditor" value={editedFiles[index]?.editedTitle || ''} onChange={(event) => handleTitleChange(index, event)} />
                        </span>
                        <span className="pb-10 flex gap-6">
                        <span className="font-bold">Keywords:</span>
                        <input className="keywordEditor" value={editedFiles[index]?.editedKeywords || ''} onChange={(event) => handleKeywordsChange(index, event)} />
                        </span>
                    </div>

                </li>
                );
            })}
            </ul>
        </div>
        <div className="flex gap-5">
            <input id="jsonEditor-submitButton" className="w-[200px] bg-purple-700 text-white rounded-sm my-4 py-1" type="submit" value="Update" />
            <input id="generateAdobeDataSubmit" className="w-[200px] bg-purple-700 text-white rounded-sm my-4 py-1" type="button" value="Generate CSV" onClick={generateCsvFile} />
            <input id="recordDownloadSubmit" className="w-[200px] bg-purple-700 text-white rounded-sm my-4 py-1" type="button" value="Download" onClick={downloadRecords} />
        </div>
    </form>
</div>



{/* BOTTOM BUTTONS */}

<form id="jsonEditor-formBody" onSubmit={handleJsonEditorSubmit} encType="multipart/form-data" className='w-full'>

    <div className="flex w-full justify-around">

        {/* UPDATE DATA OPTION */}

        <div id="jsonEditor-submitButton" className="relative mx-auto h-16 w-72 flex justify-center items-center group transition-opacity duration-300 ease-in-out" onClick={handleJsonEditorSubmit}>

            <div className="ring-2 ring-green-600 h-16 w-72 items-center rounded-xl shadow-lg hover:shadow-xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
            </div>
            <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-100">Update Data</p>
            <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

        {/* GENERATE CSV OPTION */}

        <div id="generateAdobeDataSubmit" className="relative mx-auto h-16 w-72 flex justify-center items-center group transition-opacity duration-300 ease-in-out" onClick={generateCsvFile}>

            <div className="ring-2 ring-green-600 h-16 w-72 items-center rounded-xl shadow-lg hover:shadow-xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
            </div>
            <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-100">Generate CSV</p>
            <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

        {/* DOWNLOAD FILES OPTION */}

        <div id="recordDownloadSubmit" className="relative mx-auto h-16 w-72 flex justify-center items-center group transition-opacity duration-300 ease-in-out" onClick={downloadRecords}>

            <div className="ring-2 ring-green-600 h-16 w-72 items-center rounded-xl shadow-lg hover:shadow-xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
            </div>
            <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-100">Download File & Images</p>
            <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

    </div>
</form>

</main>
)
};

export default UploadPopup;
