import React, { useState, useEffect } from 'react'
import NavBar from '../components/global/NavBar'
import Footer from '../components/global/Footer'
import OtherProducts from '../components/global/OtherProducts'
import UpscaleImageInput from '../components/upscale-upload/UpscaleImageInput'
import ListLookView from '../components/upscale-upload/UpscaleListLookView'

const UpscaleUploadPage = () => {

  const [isUpscaled, setIsUpscaled] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isQualitySet, setIsQualitySet] = useState(false);
  
  const onUploadClicked = (files, upscaled, quality) => {
    // Add a loading state to each file object
    const filesWithLoading = files.map(file => ({ ...file, isLoading: true }));
    setUploadedFiles(filesWithLoading);
    setIsUpscaled(upscaled);
    setIsQualitySet(quality);

    // Here, you would have your logic to process the files
    // Once a file is processed, update its loading state:
    filesWithLoading.forEach((file, index) => {
      // Replace this with actual file processing completion logic
      processFile(file).then(() => {
        // Update the loading state once the file is processed
        setUploadedFiles(currentFiles => currentFiles.map((f, i) => 
          i === index ? { ...f, isLoading: false } : f
        ));
      });
    });
  };
  
  useEffect(() => {
      if (uploadedFiles.length > 0) {
          onUploadClicked(uploadedFiles, isUpscaled, isQualitySet);
      }
  }, [uploadedFiles, isUpscaled, isQualitySet]);

  return (
    <div>
      <NavBar />
      <UpscaleImageInput onUploadClicked={onUploadClicked} />
      <ListLookView 
        uploadedFiles={uploadedFiles} 
        isUpscaled={isUpscaled} 
        isQualitySet={isQualitySet} 
      />
      <OtherProducts />
      <Footer />
    </div>
  )
}

export default UpscaleUploadPage
