import "../index.css";
import React, { useEffect, useRef } from 'react';

import uploadIcon from '../assets/upload-icon.png';

function UploadPopup() {
    const dropzoneRef = useRef(null);
    const fileInputRef = useRef(null);
    const fileListRef = useRef(null);
  
    useEffect(() => {
        const dropzone = dropzoneRef.current;
        const fileInput = fileInputRef.current;
  
        const handleDragOver = (e) => {
            e.preventDefault();
            dropzone.classList.add('border-blue-500', 'border-2');
        };
  
        const handleDragLeave = () => {
            dropzone.classList.remove('border-blue-500', 'border-2');
        };
  
        const handleDrop = (e) => {
            e.preventDefault();
            dropzone.classList.remove('border-blue-500', 'border-2');
  
            const files = e.dataTransfer.files;
            handleFiles(files);
        };
  
        const handleChange = (e) => {
            const files = e.target.files;
            handleFiles(files);
        };
  
        // Attach the event listeners
        dropzone.addEventListener('dragover', handleDragOver);
        dropzone.addEventListener('dragleave', handleDragLeave);
        dropzone.addEventListener('drop', handleDrop);
        fileInput.addEventListener('change', handleChange);
  
        // Cleanup the event listeners
        return () => {
            dropzone.removeEventListener('dragover', handleDragOver);
            dropzone.removeEventListener('dragleave', handleDragLeave);
            dropzone.removeEventListener('drop', handleDrop);
            fileInput.removeEventListener('change', handleChange);
        };
    }, []);
  
    function handleFiles(files) {
        const fileList = fileListRef.current;
        fileList.innerHTML = '';
  
        for (const file of files) {
            const listItem = document.createElement('div');
            listItem.textContent = `${file.name} (${formatBytes(file.size)})`;
            fileList.appendChild(listItem);
        }
    }
    
    function formatBytes(bytes) {
        if (bytes === 0) return '0 Bytes';
    
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
    
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    }
  
    return (
        <main className="absolute top-5 z-[999] bg-green-100 rounded-xl flex justify-around items-center flex-col min-h-[95vh] w-[95vw] px-20">

            {/* POPUP HEADER */}
            <div>
                <div className="flex text-white drop-shadow-lg w-full">
                    <h3 className="text-[40px] mr-4 font-bold">Title -</h3>
                    <h4 className="text-[30px] self-center mt-1 font-semibold underline">Subheading</h4>
                </div>
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quia vel ducimus autem? Qui, cum corporis vero provident eaque impedit eligendi consectetur laborum odio, nam sunt, aliquam ut reiciendis nihil exercitationem!</p>
            </div>

            
            {/* IMAGE INPUT */}
            <div className="w-full max-w-[800px] p-9 bg-white rounded-lg shadow-lg lg:self-end self-center">
                <h1 className="text-center text-2xl sm:text-2xl font-semibold mb-4 text-gray-800">File Drop and Upload</h1>
                <div
                    ref={dropzoneRef}
                    className="bg-gray-100 p-8 text-center rounded-lg border-dashed border-2 border-gray-300 hover:border-green-600 transition duration-300 ease-in-out transform hover:shadow-md group"
                >
                    <label htmlFor="fileInput" className="cursor-pointer flex flex-col items-center space-y-2">
                        <svg className="w-16 h-16 text-gray-400 group-hover:text-green-600 transition duration-300 ease-in-out transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
                        </svg>
                        <span className="text-gray-600">Drag and drop your files here</span>
                        <span className="text-gray-500 text-sm">(or click to select)</span>
                    </label>
                    <input ref={fileInputRef} type="file" className="hidden" multiple />
                </div>
                <div className="mt-6 text-center" ref={fileListRef}></div>
            </div>

            <div className="">

                {/* AI UPSCALE OPTION */}
      
        <div className="relative mx-auto h-16 w-64 flex justify-center items-center group transition-opacity duration-300 ease-in-out" >

        <div className="ring-2 ring-green-600 h-16 w-64 items-center rounded-xl shadow-2xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
        </div>
        <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-300">AI Upscale Images</p>
        <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

        {/* AI GENERATE KEYWORDS OPTION */}

        <div className="relative mx-auto h-16 w-64 flex justify-center items-center group transition-opacity duration-300 ease-in-out" >

        <div className="ring-2 ring-green-600 h-16 w-64 items-center rounded-xl shadow-2xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
        </div>
        <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-300">AI Generate Keywords</p>
        <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

        {/* AI GENERATE TITLE OPTION */}

        <div className="relative mx-auto h-16 w-64 flex justify-center items-center group transition-opacity duration-300 ease-in-out" >

        <div className="ring-2 ring-green-600 h-16 w-64 items-center rounded-xl shadow-2xl cursor-pointer absolute overflow-hidden transform hover:scale-x-110 transition duration-300 ease-in-out">
        </div>
        <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-300">AI Generate Title</p>
        <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />

        </div>

        </div>

    </main>
    )
};

export default UploadPopup;
