      }
    }
  }, [selected]);

  useEffect(() => {
    if (selected !== null && sliderVideoRef.current) {
      const slider = sliderVideoRef.current;
      const selectedElement = slider.querySelector(`[data-item="${selected}"]`);
  
      if (selectedElement) {
        // Calculate the position to scroll to
        const scrollPosition = selectedElement.offsetLeft - slider.offsetLeft;
  
        slider.scrollTo({
          left: scrollPosition,
          behavior: 'smooth'
        });
      }
    }
  }, [selected]);
  

  return (
    <div>
      {/* NAV */}

        <div id="upscaleGlobal_nav" className="sticky top-0 h-[70px] w-full flex justify-between items-center bg-stone-950 px-6 border-b-[1px] border-gray-500">
          
          <img src="https://cadogy.com/assets/cadogy-shield-cf2a447d.svg" height="60" width="60" alt="" />
          
          <div className="flex gap-12 items-center">
            <div className="flex gap-16 font-bold text-gray-300">
              <span>
                <p>Upscale Images & Videos</p>
              </span>
              <span>
                <p>Features</p>
              </span>
              <span>
                <p>FAQs</p>
              </span>
              <span>
                <p>Blog</p>
              </span>

            </div>
            <div className="flex gap-2 text-[16px]">
              <span className="px-5 py-2 rounded-lg hover:bg-stone-900">
                <p className="text-gray-500">Login</p>
              </span>
              <span className="bg-white px-5 py-2 rounded-lg bg-gradient-to-tl from-purple-500 to-pink-500 text-white font-bold">
                <p className="text-gray-300">Signup</p>
              </span>
            </div>
          </div>
          
        </div>

      {/* HERO */}
        <div id="upscaleHome_hero" className="w-full flex justify-around items-center bg-stone-950">
          
          <div id="upscaleHome_hero-left" className="w-full flex justify-center">
            
            <video autoPlay loop className="w-full max-w-[600px]">
              <source src="https://cdn.pixelbin.io/v2/dummy-cloudname/original/erasebg_assets/upload_page/EraseBG_Hero.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            
          </div>
          <div id="upscaleHome_hero-right" className="w-full my-16">
            <div>
              <h1 className="text-white font-bold text-[56px] max-w-[650px] leading-none">Upscale Image & Video Quality For <span className="bg-gradient-to-tl from-purple-500 to-pink-500 text-transparent bg-clip-text">Free</span></h1>
              <h3 className="text-white text-[28px] max-w-[650px] my-4">Experience accurate background removal. Sign up today to avail your first 3 credits for free!</h3>
            </div>
            
            {/* uploader here */}
            <img src="https://i.gyazo.com/44edfdbeb3946b2a12827479cd831774.png" alt="" srcset="" className="max-w-[650px] mt-20" />
          
          </div>
          
        </div>
      {/* SLIDER IMAGE */}

        <div id="upscaleHome_photoSlider" className="flex flex-col items-center bg-stone-950">
          
          <div id="upscaleHome_photoSlider-header" className="flex flex-col w-full items-center">
            
            <h3 className="font-bold text-[44px] text-white">Not Just Any Other Background Removal Tool</h3>
            <p className="font-bold text-[20px] text-white">Get precise and quick image transformations regardless of what industry you belong to!</p>
            
          </div>
          
          <div id="upscaleHome_photoSlider-selectSlider" className="flex overflow-x-auto gap-28 max-w-[60vw] hide-scrollbar text-white my-16 border-b-[1px] border-gray-500" ref={sliderPhotoRef}>
          {photoItems.map((item, index) => (
            <p
              key={index}
              data-item={item}
              onClick={() => handleSelect(item)}
              className={`cursor-pointer px-3 py-2 my-0 min-w-[140px] ${selected === item ? 'border-b-8 border-purple-500 font-bold' : ''}`}
            >
              {item}
            </p>
          ))}
          </div>
          
          <div id="upscaleHome_photoSlider-imageSlider">
            
            <img src="https://i.gyazo.com/d543dca3226cd06b09de7e9c6b93b7e8.png" alt="" />
            
          </div>

        </div>

      {/* BACK-AND-FORTH PIECES */}

        <div id="upscaleHome_pieces" className="w-full flex flex-col items-center bg-stone-950 mx-">
          
          <div id="upscaleHome_pieces-header" className="flex flex-col items-center py-20 px-6">
            
            <h3 className="font-bold text-[44px] text-white">Enterprise Solutions at Scale</h3>
            <p className="font-bold text-[20px] text-white">Explore our suite of extended capabilities that ease up your regular workflows</p>
          
          </div>
          <div id="upscaleHome_pieces-top" className="w-full flex md:flex-row flex-col justify-center items-center md:gap-32 px-6">
            
            <div className="flex justify-center">
              <img src="https://cdn.pixelbin.io/v2/dummy-cloudname/original/erasebg_assets/home_page/illustrations-01.png" className="md:max-h-none max-h-[400px]" alt="" />
            </div>
            
            <div className="flex flex-col justify-center">
              <h4 className="font-bold text-[44px] text-white">Bulk Transformation</h4>
              <p className="max-w-[52ch] text-[20px] text-white">No more switching back and forth to transform images. Experience bulk background removal on a single platform by uploading multiple images at once.</p>
            </div>
            
          </div>
          <div id="upscaleHome_pieces-bottom" className="w-full flex md:flex-row-reverse flex-col  justify-center items-center  md:gap-32 px-6">
            
            <div className="flex justify-center">
              <img src="https://cdn.pixelbin.io/v2/dummy-cloudname/original/erasebg_assets/home_page/illustrations-02.png" alt="" />
            </div>
            
            <div className="flex flex-col justify-center">
              <h4 className="font-bold text-[44px] text-white">API Integration</h4>
              <p className="max-w-[52ch] text-[20px] text-white">Speed up your workflows and enhance productivity by seamlessly integrating our APIs into your existing organization.</p>
            </div>
            
          </div>

        </div>

      {/* SLIDER VIDEO */}

        <div id="upscaleHome_videoSlider" className="flex flex-col items-center bg-stone-950">
            
            <div id="upscaleHome_videoSlider-header" className="flex flex-col w-full items-center">
              
              <h3 className="font-bold text-[44px] text-white">Not Just Any Other Background Removal Tool</h3>
              <p className="font-bold text-[20px] text-white">Get precise and quick image transformations regardless of what industry you belong to!</p>
              
            </div>
            
            <div id="upscaleHome_videoSlider-selectSlider" className="flex overflow-x-auto gap-28 max-w-[60vw] hide-scrollbar text-white my-16 border-b-[1px] border-gray-500" ref={sliderVideoRef}>
            {videoItems.map((item, index) => (
              <p
                key={index}
                data-item={item}
                onClick={() => handleSelect(item)}
                className={`cursor-pointer px-3 py-2 my-0 min-w-[140px] ${selected === item ? 'border-b-8 border-purple-500 font-bold' : ''}`}
              >
                {item}
              </p>
            ))}
            </div>
            
            <div id="upscaleHome_videoSlider-imageSlider">
              
              <img src="https://i.gyazo.com/d543dca3226cd06b09de7e9c6b93b7e8.png" alt="" />
              
            </div>

          </div>

      {/* BLOGS (as reviews) */}
      {/* HOW-TO GUIDES */}
      {/* OTHER PRODUCTS */}
      {/* FAQs */}
      {/* BULLSHIT */}
      {/* FOOTER */}
    </div>
  )
}

export default App
