import React, { useEffect, useState } from 'react';
import discord from '../../assets/discord.png';
import facebook from '../../assets/facebook.png';
import instagram from '../../assets/instagram.png';
import messenger from '../../assets/messenger.png';
import pinterest from '../../assets/pinterest.png';
import reddit from '../../assets/reddit.png';
import twitter from '../../assets/twitter.png';
import whatsapp from '../../assets/whatsapp.png';
import discordhover from '../../assets/discordhovered.png';
import facebookhover from '../../assets/facebookhovered.png';
import instagramhover from '../../assets/instagramhovered.png';
import messengerhover from '../../assets/messengerhovered.png';
import pinteresthover from '../../assets/pinteresthovered.png';
import reddithover from '../../assets/reddithovered.png';
import twitterhover from '../../assets/twitterhovered.png';
import whatsapphover from '../../assets/whatsapphovered.png';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet }) {
  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  // Initialize selectedQuality state with 'upscaled' for all items
  const [selectedQuality, setSelectedQuality] = useState({});

  // State to track hovered icon
  const [hoveredIcon, setHoveredIcon] = useState('');

  // Array of social media information
  const socialMedia = [
    { name: 'discord', src: discord, alt: discordhover },
    { name: 'facebook', src: facebook, alt: facebookhover },
    { name: 'instagram', src: instagram, alt: instagramhover },
    { name: 'messenger', src: messenger, alt: messengerhover },
    { name: 'pinterest', src: pinterest, alt: pinteresthover },
    { name: 'reddit', src: reddit, alt: reddithover },
    { name: 'twitter', src: twitter, alt: twitterhover },
    { name: 'whatsapp', src: whatsapp, alt: whatsapphover },
  ];

  // Set the default value for selectedQuality based on the index
  useEffect(() => {
    const defaultQuality = 'upscaled';
    const defaultSelectedQuality = {};
    uploadedFiles.forEach((_, index) => {
      defaultSelectedQuality[index] = defaultQuality;
    });
    setSelectedQuality(defaultSelectedQuality);
  }, [uploadedFiles]);

  // Function to handle quality choice button click
  const handleQualityChoiceClick = (index, quality) => {
    setSelectedQuality({ ...selectedQuality, [index]: quality });
  };

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full bg-stone-950">
      <ul id="listLiveView-container" className="w-full h-full flex flex-col items-center justify-center gap-8">
        {uploadedFiles.map((file, index) => {
          const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
          const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

          return (
            <li key={index} className="flex flex-col max-w-[1300px] mx-4 xl:mx-20 w-full h-full bg-blue-50 py-20 px-12 rounded-md">
              {/* Image and Sidebar Views */}
              <div className='flex justify-around gap-20 w-full'>
                {/* Image Views */}
                <div className='flex justify-around gap-10 w-[75%]'>
                  {/* Original Image */}
                  <ImageDisplay 
                    title="Original Image" 
                    imageUrl={originalImageUrl} 
                    altText={file.originalFilename} 
                  />
                  {/* Upscaled Image */}
                  <ImageDisplay 
                    title="Upscaled Image" 
                    imageUrl={upscaledImageUrl} 
                    altText={file.upscaledFilename} 
                  />
                </div>

                {/* Sidebar */}
                <div className='w-[25%] flex flex-col justify-around items-center gap-8'>
                  {/* Quality Choice Buttons */}
                  <QualityChoiceButtons 
                    index={index} 
                    selectedQuality={selectedQuality} 
                    onQualityChoiceClick={handleQualityChoiceClick} 
                  />

                  {/* Sidebar Content for Original */}
                  <SidebarContent 
                    file={file} 
                    width={file.originalWidth} 
                    height={file.originalHeight} 
                    isVisible={selectedQuality[index] === 'original'}
                    socialMedia={socialMedia}
                    hoveredIcon={hoveredIcon}
                    setHoveredIcon={setHoveredIcon}
                  />

                  {/* Sidebar Content for Upscaled */}
                  <SidebarContent 
                    file={file} 
                    width={file.upscaledWidth} 
                    height={file.upscaledHeight} 
                    isVisible={selectedQuality[index] === 'upscaled'}
                    socialMedia={socialMedia}
                    hoveredIcon={hoveredIcon}
                    setHoveredIcon={setHoveredIcon}
                  />
                </div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

// Component to display each image
const ImageDisplay = ({ title, imageUrl, altText }) => (
  <div className='flex flex-col items-center gap-5 w-full h-full'>
    <h3 className='w-full max-w-[21.25em] font-bold text-[30px]'>{title}</h3>
    <img src={imageUrl} alt={altText} className="object-cover w-full h-full max-w-[40em] max-h-[30em]" />
  </div>
);

// Component for quality choice buttons
const QualityChoiceButtons = ({ index, selectedQuality, onQualityChoiceClick }) => (
  <div className='flex justify-center w-full mx-4 md:mx-20 gap-5'>
    <QualityButton 
      label="Original" 
      isSelected={selectedQuality[index] === 'original'} 
      onClick={() => onQualityChoiceClick(index, 'original')} 
    />
    <QualityButton 
      label="Upscaled" 
      isSelected={selectedQuality[index] === 'upscaled'} 
      onClick={() => onQualityChoiceClick(index, 'upscaled')} 
    />
  </div>
);

// Component for individual quality buttons
const QualityButton = ({ label, isSelected, onClick }) => (
  <h4
    className={`flex justify-center items-center hover:shadow-lg shadow-md py-2 px-6 rounded-3xl font-semibold text-[20px] text-white bg-indigo-300 hover:bg-indigo-400 transition-all duration-200 ease-in-out ${
      isSelected ? 'bg-indigo-600 hover:bg-indigo-600 cursor-default' : 'cursor-pointer'
    }`}
    onClick={onClick}
  >
    {label}
  </h4>
);

// Component for sidebar content
const SidebarContent = ({ file, width, height, isVisible, socialMedia, hoveredIcon, setHoveredIcon }) => {
  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;
  const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
  const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

  const downloadUrl = isVisible && file ? (isVisible === 'original' ? originalImageUrl : upscaledImageUrl) : '#';
  const downloadFilename = isVisible === 'original' ? file.originalFilename : file.upscaledFilename;

  return (
    <div className="w-full flex-col justify-between gap-4" style={{ display: isVisible ? 'flex' : 'none' }}>
      <div className='flex flex-col items-center w-full gap-2'>
        <a href={downloadUrl} download={downloadFilename} className='px-6 py-2 bg-indigo-300 ring-[2.5px] ring-indigo-500 font-semibold text-indigo-600 rounded-lg hover:bg-indigo-600 hover:text-stone-200 hover:ring-indigo-600 transition-all duration-200 ease-in-out cursor-pointer w-full text-center'>
          Download
        </a>
        <p>{width} x {height}</p>
      </div>
      <div className='flex flex-wrap justify-between gap-x-4 gap-y-4'>
        {socialMedia.map(media => (
          <SocialMediaIcon 
            key={media.name}
            media={media}
            hoveredIcon={hoveredIcon}
            setHoveredIcon={setHoveredIcon}
          />
        ))}
      </div>
    </div>
  );
};

// Component for social media icons
const SocialMediaIcon = ({ media, hoveredIcon, setHoveredIcon }) => (
  <div>
    <a href="">
      <img
        src={hoveredIcon === media.name ? media.alt : media.src}
        className='p-2.5 rounded-full shadow-md hover:shadow-lg'
        width={50}
        alt={media.name}
        onMouseEnter={() => setHoveredIcon(media.name)}
        onMouseLeave={() => setHoveredIcon('')}
      />
    </a>
  </div>
);

export default ListLookView;
