import React, { useEffect, useState } from 'react';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet }) {
  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  // State to track the selected quality for each item
  const [selectedQuality, setSelectedQuality] = useState({});

  const handleQualityChoiceClick = (index, quality) => {
    setSelectedQuality({ ...selectedQuality, [index]: quality });
  };

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full bg-stone-950">
      <ul id="listLiveView-container" className="w-full h-full flex justify-center">
        {uploadedFiles.map((file, index) => {
          let originalFilename = file.originalFilename;
          let upscaledFolder = file.upscaledFilename;
          
          const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
          const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

          // Determine which view to show based on the selected quality
          const showOriginalView = selectedQuality[index] === 'original';
          const showUpscaledView = selectedQuality[index] === 'upscaled';

          return (
            <li id={`listLiveView-item${index}`} key={index} className="flex flex-col max-w-[1600px] mx-4 lg:mx-20 w-full h-full bg-blue-50 gap-5">
              <div className='flex justify-around gap-5 w-full'>
                <div className='flex justify-around gap-5 w-[75%]'>
                  <div id={`listLiveView-item${index}-original`} className={`flex flex-col items-center gap-5 w-full h-full ${showOriginalView ? '' : 'hidden'}`}>
                    <h3 className='w-full max-w-[21.25em] font-bold text-[30px]'>Original Image</h3>
                    <img src={originalImageUrl} alt={originalFilename} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
                  </div>
                  <div id={`listLiveView-item${index}-upscaled`} className={`flex flex-col items-center gap-5 w-full h-full ${showUpscaledView ? '' : 'hidden'}`}>
                    <h3 className='w-full max-w-[21.25em] font-bold text-[30px]'>Upscaled Image</h3>
                    <img src={upscaledImageUrl} alt={upscaledFolder} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
                  </div>
                </div>
                
                <div id={`listLiveView-item${index}-sidebar`} className=' w-[25%]'>
                  <div id={`listLiveView-item${index}-qualityChoice`} className='flex w-full mx-4 lg:mx-20 gap-5'>
                    <h4
                      id={`listLiveView-item${index}-qualityChoice-original`}
                      className={`flex justify-center items-center shadow-md py-2 px-6 rounded-3xl font-semibold text-[20px] ${selectedQuality[index] === 'original' ? 'bg-indigo-300' : ''}`}
                      onClick={() => handleQualityChoiceClick(index, 'original')}
                    >
                      Original
                    </h4>
                    <h4
                      id={`listLiveView-item${index}-qualityChoice-upscaled`}
                      className={`flex justify-center items-center shadow-md py-2 px-6 rounded-3xl font-semibold text-[20px] ${selectedQuality[index] === 'upscaled' ? 'bg-indigo-300' : ''}`}
                      onClick={() => handleQualityChoiceClick(index, 'upscaled')}
                    >
                      Upscaled
                    </h4>
                  </div>
                  <div id={`listLiveView-item${index}-sidebar-original`}>Download</div>
                  <div id={`listLiveView-item${index}-sidebar-upscaled`}>Hello</div>
                </div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ListLookView;
