import React, { useEffect, useState } from 'react';

function ListLookView({ uploadedFiles, isUpscaled, isTitleSet }) {
  const [qualityView, setQualityView] = useState('originalView'); // Set the initial quality view to 'originalView'

  const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

  // Event handler for when the "Original" quality choice is selected
  const handleOriginalClick = () => {
    setQualityView('originalView'); // Set qualityView to 'originalView'
  };

  // Event handler for when the "Upscaled" quality choice is selected
  const handleUpscaledClick = () => {
    setQualityView('upscaledView'); // Set qualityView to 'upscaledView'
  };

  return (
    <div id="listLiveView" encType="multipart/form-data" className="w-full bg-stone-950">
      <ul id="listLiveView-container" className="w-full h-full flex justify-center">
        {uploadedFiles.map((file, index) => {
          let originalFilename = file.originalFilename;
          let upscaledFolder = file.upscaledFilename;
          
          const originalImageUrl = `${serverOrigin}/uploads/${file.originalFilename}`;
          const upscaledImageUrl = `${serverOrigin}/upscaled/${file.upscaledFilename}`;

          return (
            <li id={`listLiveView-item${index}`} key={index} className="flex flex-col max-w-[1600px] mx-4 lg:mx-20 w-full h-full bg-blue-50 gap-5">
              <div id={`listLiveView-item${index}-qualityChoice`} className='flex w-full mx-4 lg:mx-20 gap-5'>
                <h4 id={`listLiveView-item${index}-qualityChoice-original`} className={`flex justify-center items-center hover:bg-indigo-300 shadow-md py-2 px-6 rounded-3xl font-semibold text-[16px] transition-all duration-200 ${qualityView === 'originalView' ? 'bg-indigo-600 text-white' : ''}`} onClick={handleOriginalClick}>
                  Original
                </h4>
                <h4 id={`listLiveView-item${index}-qualityChoice-upscaled`} className={`flex justify-center items-center hover:bg-indigo-300 shadow-md py-2 px-6 rounded-3xl font-semibold text-[16px] transition-all duration-200 ${qualityView === 'upscaledView' ? 'bg-indigo-600 text-white' : ''}`} onClick={handleUpscaledClick}>
                  Upscaled
                </h4>
              </div>
              <div id={`listLiveView-item${index}-photoBox`} className='flex'>
                {qualityView === 'originalView' ? ( // Render the original photo box when 'originalView' is selected
                  <div id={`listLiveView-item${index}-photoBox-original`} className='flex flex-col items-center gap-5 w-full h-full'>
                    <img src={originalImageUrl} alt={originalFilename} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
                  </div>
                ) : (
                  <div id={`listLiveView-item${index}-photoBox-upscaled`} className='flex flex-col items-center gap-5 w-full h-full'>
                    <img src={upscaledImageUrl} alt={upscaledFolder} className="object-cover w-full h-full max-w-[40em] max-h-[30em] rounded-md" />
                  </div>
                )}
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ListLookView;
