import React, { useRef, useState, useEffect } from 'react';
import uploadIcon from '../../assets/upload-icon.png';

/**
 * Component for updating image input with drag and drop functionality.
 * @param {Function} onUploadClicked - Callback function triggered on upload.
 */
function UpdateImageInput({ onUploadClicked }) {
    // Refs for DOM elements
    const dropzoneRef = useRef(null);
    const fileInputRef = useRef(null);
    const fileListRef = useRef(null);

    // State variables
    const [uploadedFiles, setUploadedFiles] = useState([]);
    const [isUpscaled, setIsUpscaled] = useState(false);
    const [isQualitySet, setIsQualitySet] = useState(false);
    const [isUploadClicked, setIsUploadClicked] = useState(false);
    const [isFileAdded, setIsFileAdded] = useState(false);

    const [editedFiles, setEditedFiles] = useState([]);
    const [isFolderUploaded, setIsFolderUploaded] = useState(false);

    // Server origin based on the current window location
    const serverOrigin = `${window.location.protocol}//${window.location.hostname}:3001`;

    // Effect hook to handle drag and drop events
    useEffect(() => {
        // Drag and drop event handlers
        const handleDragEnter = (e) => {
            e.preventDefault();
            if (!isUploadClicked) {
                dropzoneRef.current.classList.add('border-green-600');
            }
        };

        const handleDragOver = (e) => {
            e.preventDefault();
            if (!isUploadClicked) {
                dropzoneRef.current.classList.add('border-green-600');
            }
        };

        const handleDragLeave = () => {
            if (!isUploadClicked) {
                dropzoneRef.current.classList.remove('border-green-600');
            }
        };

        const handleDrop = (e) => {
            e.preventDefault();
            if (!isUploadClicked) {
                dropzoneRef.current.classList.remove('border-green-600');
            }
            handleFiles(e.dataTransfer.files);
        };

        const handleChange = (e) => {
            handleFiles(e.target.files);
            // Call handleSubmit here, as files have been selected
            handleSubmit(e);
        };

        // DOM elements
        const dropzone = dropzoneRef.current;

        // Event listeners for drag and drop
        dropzone.addEventListener('dragenter', handleDragEnter);
        dropzone.addEventListener('dragover', handleDragOver);
        dropzone.addEventListener('dragleave', handleDragLeave);
        dropzone.addEventListener('drop', handleDrop);
        fileInputRef.current.addEventListener('change', handleChange);

        // Cleanup: remove event listeners
        return () => {
            dropzone.removeEventListener('dragenter', handleDragEnter);
            dropzone.removeEventListener('dragover', handleDragOver);
            dropzone.removeEventListener('dragleave', handleDragLeave);
            dropzone.removeEventListener('drop', handleDrop);
            fileInputRef.current.removeEventListener('change', handleChange);
        };
    }, [isUploadClicked, dropzoneRef, fileInputRef]);

    // Function to handle uploaded files
    const handleFiles = async (files) => {
        // Clear file list display
        fileListRef.current.innerHTML = '';
        let totalSize = 0;
        let isFolder = false;

        // Check if any of the files represent a folder
        for (const file of files) {
            totalSize += file.size;
            if (file.webkitRelativePath) {
                isFolder = true;
            }
        }

        // Set state based on whether a folder is uploaded
        setIsFolderUploaded(isFolder);

        if (isFolder) {
            // Display folder name and total size
            const folderName = files[0].webkitRelativePath.split('/')[0];
            fileListRef.current.textContent = `${folderName} (${formatBytes(totalSize)})`;
        } else {
            // Display each file name and size
            for (const file of files) {
                const listItem = document.createElement('div');
                const maxNameLength = 33;
                let truncatedName =
                    file.name.length > maxNameLength ? file.name.substring(0, maxNameLength) + '...' : file.name;
                listItem.textContent = `${truncatedName} (${formatBytes(file.size)})`;
                fileListRef.current.appendChild(listItem);
            }
        }

        // Set state to indicate whether files are added
        setIsFileAdded(files.length > 0);

        // Automatically send files to the server for processing
        const formData = new FormData();
        for (const file of files) {
            formData.append('files', file);
        }

        try {
            // Send a POST request to the server
            const response = await fetch(`${serverOrigin}/processFiles`, {
                method: 'POST',
                body: formData,
            });
    
            if (response.ok) {
                console.log('Files processed successfully');
                // Parse and set the processed data
                const processedData = await response.json();
                console.log('Processed Data:', processedData);
    
                setUploadedFiles(processedData);
    
                // Call onUploadClicked with uploaded data and settings
                const upscaleImages = 'true';
                const qualitySet = 'true';
                onUploadClicked(processedData, upscaleImages, qualitySet);
            } else {
                console.error('Failed to process files');
                // Handle error UI feedback
            }
        } catch (error) {
            console.error('Error processing files:', error);
            // Handle error UI feedback
        }
    };

    // Function to format bytes into readable size
    const formatBytes = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };


    // Function to handle form submission
const handleSubmit = async (event) => {
    event.preventDefault();

    // Create FormData from the form
    const formData = new FormData(document.getElementById('folderUpload-formBody'));

    // Set isUpscaled and isQualitySet based on checkbox values
    const upscaleImages = 'true';
    const qualitySet = 'true';

    try {
        // Send a POST request to upload files
        const response = await fetch('http://localhost:3001/uploads', {
            method: 'POST',
            body: formData,
            // Include the headers to specify the content type
            headers: {
                'Accept': 'application/json',
            },
        });

        if (response.ok) {
            console.log('Folder uploaded successfully');
            // Get the response body
            const text = await response.text();
            console.log("Server Response:", text);

            // Fetch updated photoList.json
            const photoListResponse = await fetch('http://localhost:3001/photoList');
            const newPhotoList = await photoListResponse.json();
            setUploadedFiles(newPhotoList);

            // Trigger the callback function with uploaded data and settings
            onUploadClicked(newPhotoList, upscaleImages, qualitySet);

            // Now initiate the image upscaling process asynchronously
            // Assuming the upscaleImage function returns a Promise
            const upscalePromises = newPhotoList.map(async (file) => {
                // Call the upscaleImage function for each file
                await upscaleImage(file, file.generatedFilename);
            });

            // Wait for all image upscaling promises to complete
            await Promise.all(upscalePromises);

            // Log a message indicating that image upscaling is complete
            console.log('Image upscaling complete');
        }
    } catch (error) {
        console.error('There was a problem with the upload:', error);
    }
};

    return (
        <div className="flex lg:flex-row flex-col w-full transition-container">
            <div className="image-input h-full w-full lg:max-w-[600px] lg:max-h-[600px] pt-5 pb-7 px-10 bg-white rounded-lg shadow-lg">

                <form id="folderUpload-formBody" onSubmit={handleSubmit} encType="multipart/form-data">

                    {/* DROP ZONE */}
                    <div
                        ref={dropzoneRef}
                        className={`bg-stone-900 px-8 py-16 text-center rounded-lg border-dashed border-2 hover:border-indigo-400 transition duration-300 ease-in-out transform hover:shadow-lg group ${isFileAdded ? 'border-indigo-400' : 'border-indigo-200'}`}
                    >
                        <label htmlFor="fileInput" className="cursor-pointer flex flex-col items-center space-y-2">
                            <svg className="w-16 h-16 text-indigo-200 group-hover:text-indigo-400 transition duration-300 ease-in-out transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
                            </svg>
                            <span className="text-indigo-200 ">Drag and drop your files here</span>
                            <span className="text-indigo-200 text-sm">(or click to select)</span>
                        </label>
                        <input id="fileInput" ref={fileInputRef} type="file" className="hidden file-list" name="folderUpload" webkitdirectory="true" directory="true" multiple />
                    </div>
                    
                    {/* REFERENCE BOX */}
                    <div className="flex flex-col w-full items-center gap-3">
                        <div className="text-center w-full text-slate-400 ring-slate-200 ring-2 py-1.5 h-[36px]" ref={fileListRef}></div>                    
                    </div>

                    {/* OPTION BUTTONS */}
                    <div className="flex flex-col w-full justify-around gap-3">
                        
                        {/* UPLOAD BUTTON */}
                        <div id="folderUpload-submitButton" className="relative mx-auto h-10 w-full flex justify-center items-center group transition-opacity duration-300 ease-in-out">
                            <input id="folderUpload-submitButton" type="submit" className="bg-green-300 py-2 h-10 w-full items-center rounded-sm shadow-lg hover:shadow-xl cursor-pointer absolute overflow-hidden transform transition duration-300 ease-in-out" disabled={!isFileAdded} />
                            <p className="text-center pl-3 pr-1 mr-2 text-green-700 font-bold z-10 pointer-events-none transition-transform duration-200 ease-in-out group-hover:-translate-x-4 h-full flex items-center ml-9 bg-green-300">Upload</p>
                            <img src={uploadIcon} alt="Upload Icon" className="cursor-pointer h-full max-h-[26px] transition duration-300 ease-in-out -translate-x-10 opacity-20 group-hover:opacity-80 group-hover:-translate-x-3 w-[28px]" />
                        </div>
                    </div>

                </form>
            </div>
        </div>
    );
};

export default UpdateImageInput;
